import { useEffect, useRef } from "react";

import styles from "./Roster.module.scss";
import { gsap } from "gsap/dist/gsap";
import { ScrollTrigger } from "gsap/dist/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);
import { faBorderNone } from "@fortawesome/free-solid-svg-icons";

export default function Roster() {
  let one = useRef(null);
  let two = useRef(null);
  let three = useRef(null);
  let four = useRef(null);
  let roster = useRef(null);
  useEffect(() => {
    let sections = [one, two, three, four];

    gsap.to(sections, {
      xPercent: -100 * (sections.length - 1),
      ease: "none",
      scrollTrigger: {
        trigger: roster,
        // markers: true,
        pin: true,
        scrub: 1,
        snap: 1 / (sections.length - 1),
        start: "top top",
        end: () => "+=" + document.querySelector("#roster").offsetWidth,
      },
    });
  });
  return (
    <section
      id="roster"
      className={` ${styles.roster} section`}
      ref={(el) => {
        roster = el;
      }}
    >
      <div
        className={`${styles.panel} ${styles.one}`}
        ref={(el) => {
          one = el;
        }}
      ></div>
      <div
        className={`${styles.panel} ${styles.two}`}
        ref={(el) => {
          two = el;
        }}
      >
        <RosterCard />
      </div>
      <div
        className={`${styles.panel} ${styles.three}`}
        ref={(el) => {
          three = el;
        }}
      >
        three
      </div>
      <div
        className={`${styles.panel} ${styles.four}`}
        ref={(el) => {
          four = el;
        }}
      >
        four
      </div>
    </section>
  );
}
