import React, { useState, useEffect } from "react";
import Link from "next/link";
import Menu from "./menu.js";

export default function Header({ history }) {
  const [state, setState] = useState({
    initial: false,
    clicked: null,
    menuName: "Menu"
  });

  const [disabled, setDisabled] = useState(false);

  // use effect for page changes
  useEffect(() => {
    // listen for page changes

    history.listen(() => {
      setState({ clicked: false, menuName: "Menu" });
    });
  });

  const handleMenu = () => {
    disableMenu();
    if (state.initial === false) {
      setState({
        initial: null,
        clicked: true,
        menuName: "Close"
      });
    } else if (state.clicked === true) {
      setState({
        clicked: !state.clicked,
        menuName: "Menu"
      });
    } else if (state.clicked === false) {
      setState({
        clicked: !state.clicked,
        menuName: "Close"
      });
    }
  };

  // Determine if our menu button should  be disabled

  const disableMenu = () => {
    setDisabled(!disabled);
    setTimeout(() => {
      setDisabled(false);
    }, 1200);
  };

  return (
    <div
      data-collapse="medium"
      data-animation="default"
      data-duration="400"
      className="nav-bar w-nav"
    >
      <Menu state={state} />
      <div className="w-container">
        <Link href="/index">
          <a
            href="index.html"
            aria-current="page"
            className="brand-link w-nav-brand w--current"
          >
            <img
              src="./logo.png"
              width="117"
              srcSet="./images/logo-p-500.png 500w, images/logo.png 640w"
              sizes="117px"
              alt="logo"
              className="image"
            />
          </a>
        </Link>
        <style jsx>{`
          .brand-link {
            width: 100%;
            z-index: 100;
          }
        `}</style>
        <nav role="navigation" className="nav-menu w-nav-menu">
          <Link href="/roster">
            <a className="nav-link w-nav-link">Roster</a>
          </Link>
          <Link href="/music">
            <a className="nav-link w-nav-link">Music</a>
          </Link>
          <Link href="/gamefilm">
            <a className="nav-link w-nav-link">GameFilm</a>
          </Link>
          <Link href="/about">
            <a className="nav-link w-nav-link">About</a>
          </Link>
          <Link href="/contact">
            <a className="nav-link w-nav-link">Contact</a>
          </Link>
        </nav>
        <div
          className="menu-button w-clearfix w-nav-button"
          disabled={disabled}
          onClick={handleMenu}
        >
          <div className="menu-text">MENU</div>
          <div className="menu-icon w-icon-nav-menu"></div>
        </div>
        <style jsx>{`
          .menu-button {
            z-index: 100;
          }
        `}</style>
      </div>
    </div>
  );
}
