import { useEffect, useRef } from "react";

import styles from "./Roster.module.scss";
import { gsap } from "gsap/dist/gsap";
import { ScrollTrigger } from "gsap/dist/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);
import { faBorderNone } from "@fortawesome/free-solid-svg-icons";

export default function Roster() {
  let one = useRef(null);
  let two = useRef(null);
  let three = useRef(null);
  let four = useRef(null);
  let roster = useRef(null);
  useEffect(() => {
    let sections = [one, two, three, four];

    gsap.to(sections, {
      xPercent: -100 * (sections.length - 1),
      ease: "none",
      scrollTrigger: {
        trigger: roster,
        markers: true,
        pin: true,
        scrub: 1,
        snap: 1 / (sections.length - 1),
        start: "top top",
        end: () => "+=" + document.querySelector("#roster").offsetWidth,
      },
    });
    sections.forEach((section, index) => {
      let tween = gsap.from(section, {
        opacity: 0,
        scale: 0.6,
        duration: 1,
        force3D: true,
        paused: true,
      });
      addSectionCallbacks(tl, {
        start: sectionIncrement * (index - 0.99),
        end: sectionIncrement * (index + 0.99),
        onEnter: () => tween.play(),
        onLeave: () => tween.reverse(),
        onEnterBack: () => tween.play(),
        onLeaveBack: () => tween.reverse(),
      });
      index || tween.progress(1); // the first tween should be at its end (already faded/scaled in)
    });
    function addSectionCallbacks(
      timeline,
      { start, end, param, onEnter, onLeave, onEnterBack, onLeaveBack }
    ) {
      let trackDirection = (animation) => {
          // just adds a "direction" property to the animation that tracks the moment-by-moment playback direction (1 = forward, -1 = backward)
          let onUpdate = animation.eventCallback("onUpdate"), // in case it already has an onUpdate
            prevTime = animation.time();
          animation.direction = animation.reversed() ? -1 : 1;
          animation.eventCallback("onUpdate", () => {
            let time = animation.time();
            if (prevTime !== time) {
              animation.direction = time < prevTime ? -1 : 1;
              prevTime = time;
            }
            onUpdate && onUpdate.call(animation);
          });
        },
        empty = (v) => v; // in case one of the callbacks isn't defined
      timeline.direction || trackDirection(timeline); // make sure direction tracking is enabled on the timeline
      start >= 0 &&
        timeline.add(
          () =>
            ((timeline.direction < 0 ? onLeaveBack : onEnter) || empty)(param),
          start
        );
      end <= timeline.duration() &&
        timeline.add(
          () =>
            ((timeline.direction < 0 ? onEnterBack : onLeave) || empty)(param),
          end
        );
    }
  });
  return (
    <section
      id="roster"
      className={` ${styles.roster} section`}
      ref={(el) => {
        roster = el;
      }}
    >
      <div
        className={`${styles.panel} ${styles.one}`}
        ref={(el) => {
          one = el;
        }}
      >
        one
      </div>
      <div
        className={`${styles.panel} ${styles.two}`}
        ref={(el) => {
          two = el;
        }}
      >
        two
      </div>
      <div
        className={`${styles.panel} ${styles.three}`}
        ref={(el) => {
          three = el;
        }}
      >
        three
      </div>
      <div
        className={`${styles.panel} ${styles.four}`}
        ref={(el) => {
          four = el;
        }}
      >
        four
      </div>
    </section>
  );
}
