import { useEffect, useRef } from "react";

import styles from "./Roster.module.scss";
import { gsap } from "gsap/dist/gsap";
import { ScrollTrigger } from "gsap/dist/ScrollTrigger";
import RosterCard from "./RosterCard/RosterCard";
import { albumData } from "./data/albumData";
import { biosData } from "./data/biosData";
gsap.registerPlugin(ScrollTrigger);

import RosterProfile from "./RosterProfile/RosterProfile";

export default function Roster() {
  // let one = useRef(null);
  // let two = useRef(null);
  // let three = useRef(null);
  // let four = useRef(null);
  // let roster = useRef(null);
  // useEffect(() => {
  //   let sections = [one, two, three, four];

  //   gsap.to(sections, {
  //     xPercent: -100 * (sections.length - 1),
  //     ease: "none",
  //     scrollTrigger: {
  //       trigger: roster,
  //       markers: true,
  //       pin: true,
  //       scrub: 1,
  //       snap: 1 / (sections.length - 1),
  //       start: "top top",
  //       end: () => "+=" + document.querySelector("#roster").offsetWidth,
  //     },
  //   });
  // });
  return (
    <section
      id="roster"
      className={` ${styles.roster} section`}
      // ref={(el) => {
      //   roster = el;
      // }}
    >
      <div
        className={`${styles.panel} ${styles.one} slide`}
        // ref={(el) => {
        //   one = el;
        // }}
      >
        <h2 className={styles.rosterTitle}>
          Our <span className={styles.titleColor}>Roster</span>
        </h2>
      </div>
      <div
        className={`${styles.panel} ${styles.player} slide`}
        // ref={(el) => {
        //   two = el;
        // }}
      >
        <RosterCard
          background="images/obHeadshot.png"
          name="Orange Beanie"
          position="Rapper"
          number="#16"
          bgColor="#006BB6"
        />
        <RosterProfile albumData={albumData[0].albums} biosData={biosData[0]} />
      </div>
      <div
        className={`${styles.panel} ${styles.player} slide`}
        // ref={(el) => {
        //   three = el;
        // }}
      >
        <RosterCard
          background="images/johs.png"
          name="Jerry Owens"
          position="Rapper"
          number="#3"
          bgColor="#ce1141"
        />
        <RosterProfile albumData={albumData[1].albums} biosData={biosData[1]} />
      </div>
      <div
        className={`${styles.panel} ${styles.player} slide`}
        // ref={(el) => {
        //   four = el;
        // }}
      >
        <RosterCard
          background="images/obHeadshot.png"
          name="Lord Ward"
          position="Producer"
          number="#2"
          bgColor="#552583"
        />
        {/* <RosterProfile /> */}
      </div>
    </section>
  );
}
